import React from "react"
import axios from "axios"

class URLInput1 extends React.Component {
    state = {
        longURL:"",
        newURL:"",
        errorMsg:""
    }

    handleGeneration(e) {
        e.preventDefault();
        console.log(this.state);
        const URLToShorten = { url: this.state.longURL };

        axios.post('http://localhost:8080/submit', {URLToShorten})
            .then(res => {
                console.log("recieved data from flask");
                this.setState((res.data.newurl) ? {newURL: res.data.newurl} : {errorMsg: res.data.error})
                console.log(this.state)
            })
    }
    render() {
        return (
            <div>
                <form>  
                    <input type="text" placeholder="Enter Long URL Here..." name="longURL" 
                        onChange={e => this.setState({longURL: e.target.value})}
                    />
                    <input type="submit" value="Generate" onClick={e => this.handleGeneration(e)}/>
                </form>
            </div>
        )
    }
}

export default URLInput1
