import React from "react"
import Heading from "./Heading"
import URLInput1 from "./URLInput1"
import URLOutput from "./URLOutput"
import ErrorMessage from "./ErrorMessage"

class WebContainer extends React.Component {
    //state contains if the error message should be displayed
    state = {
        errorDisplay: "", //update message of the error
        miniURL: "", //update shortened version of the URL through Flask and fetch
    }
    render() {
        return (
            <div>
                <Heading />
                <URLInput1 />
                <URLOutput newurl = {this.state.miniURL} />
                <ErrorMessage display= {this.state.errorDisplay} />
            </div>
        )
    }
}
export default WebContainer
